package com.android.xjq.utils.recharge;import android.text.TextUtils;import java.util.HashMap;import java.util.Map;public class PayResult {    private String resultStatus;    private String result;    private String memo;    private static HashMap<String, String> map = new HashMap<>();    static {        map.put("8000", "正在处理中(8000)");        map.put("4000", "订单支付失败(4000)");        map.put("6001", "用户中途取消(6001)");        map.put("6002", "网络连接出错(6002)");    }    public PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        if (map.get(resultStatus) != null) {            return map.get(resultStatus);        }        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }}